mapper.select:
SELECT A.NO ,PER.NAME AS PERMISSION ,PO.NAME AS POSITION ,A.ID ,A.PWD ,A.NICK ,A.NAME ,A.PHONE ,A.ENROLL_DATE ,A.MODIFY_DATE ,A.QUIT_YN ,A.PROFILE_NAME FROM ADMIN A JOIN ADMIN_PERMISSION PER ON A.PERMISSION = PER.NO JOIN ADMIN_POSITION PO ON A.POSITION = PO.NO
A.ID = #{id}
AND A.NICK = #{nick}

mapper.insert:
INSERT INTO ADMIN
(
    NO
    , PERMISSION
    , POSITION
    , ID
    , PWD
    , NICK
    , NAME
    , PHONE
)
VALUES
(
    SEQ_ADMIN_NO.NEXTVAL
    , #{permission}
    , #{position}
    , #{id}
    , #{pwd}
    , #{nick}
    , #{name}
    , #{phone}
)

mapper.update:
UPDATE ADMIN
    SET

    NICK = #{nick}	    


    ,PHONE = #{phone}	    


    ,PWD = #{pwd}


	,POSITION = #{position}


	,PERMISSION = #{permission}


	,PROFILE_NAME = #{profileName}

WHERE NO = #{no}

mapper.insert:
INSERT INTO POINT 
(
