    NICK = #{nick}	    


    ,PHONE = #{phone}	    


    ,PWD = #{pwd}


	,POSITION = #{position}


	,PERMISSION = #{permission}


	,PROFILE_NAME = #{profileName}

WHERE NO = #{no}

