mapper.select:
SELECT CLASS_NAME AS LABEL , STUDENT_CNT AS DATA FROM ( SELECT CLASS_NAME, STUDENT_CNT FROM CLASS
WHERE CATEGORY = #{cateNo}
ORDER BY STUDENT_CNT DESC ) WHERE ROWNUM <= 6

