mapper.insert:
insert into CART
(
CART_NO
, MEMBER_NO
, CLASS_NO
)
values
(
SEQ_CART_NO.NEXTVAL
  ,#{memberNo}
  , #{classNo}
)

insert into WISH
(
MEMBER_NO
, CLASS_NO
)
values
(
  #{memberNo}
  , #{classNo}
)

mapper.delete:

delete from cart 
where MEMBER_NO = #{memberNo}
and CLASS_NO = #{classNo}
