mapper.insert:
  	INSERT INTO CORPORATE_MEMBER
	(
	    NO
		, ID
		, PWD
		, COMPANY_NAME
		, NAME
		, PHONE
		, ENROLL_DATE
	)
	VALUES
	(	
		SEQ_CORPORATE_MEMBER_NO.NEXTVAL
		 , #{id}
		 , #{pwd}
		 , #{companyName}
		 , #{name}
		 , #{phone}
		 , SYSDATE
	)

mapper.update:
UPDATE CORPORATE_MEMBER
SET
	COMPANY_NAME = #{companyName}
	, COMPANY_NUM = #{companyNum}
	, COMPANY_ADDRESS = #{companyAddress}
	, INTRODUCE = #{introduce}
	, SECTOR = #{sector}
	, EMP_NUM = #{empNum}
	, SALES = #{sales}
	, HOMEPAGE = #{homepage}
WHERE NO = #{no}
