mapper.insert:
  	INSERT INTO CORPORATE_MEMBER
	(
	    NO
		, ID
		, PWD
		, COMPANY_NAME
		, NAME
		, PHONE
		, ENROLL_DATE
	)
	VALUES
	(	
		SEQ_CORPORATE_MEMBER_NO.NEXTVAL
		 , #{id}
		 , #{pwd}
		 , #{companyName}
		 , #{name}
		 , #{phone}
		 , SYSDATE
	)

mapper.select:
SELECT ID 
FROM CORPORATE_MEMBER 
WHERE ID = #{id}

  	SELECT CM.*
	FROM CORPORATE_MEMBER CM
	WHERE CM.ID = #{id}

mapper.update:
UPDATE CORPORATE_MEMBER
SET
	COMPANY_NAME = #{companyName}
	, COMPANY_NUM = #{companyNum}
	, COMPANY_ADDRESS = #{companyAddress}
	, DETAIL_ADDRESS = #{detailAddress}
	, INTRODUCE = #{introduce}
	, SECTOR = #{sector}
	, EMP_NUM = #{empNum}
	, SALES = #{sales}
	, HOMEPAGE = #{homepage}
	, LOGO = #{logoName}
	, THUMB = #{thumbName}
WHERE NO = #{no}

mapper.insert:
  	INSERT INTO EMPLOYMENT
  	(
	    NO
		, CORP_NO
		, CATEGORY
		, TITLE
		, RESPONSIBILITY
		, QUALIFICATION
		, PREFERENTIAL
		, SALARY
		, BENEFITS
		, INTRODUCE
		, SECTOR
		, ADDRESS
		, CAREER
		, DEADLINE
	)
	VALUES
	(	
		SEQ_EMPLOYMENT_NO.NEXTVAL
		 , #{corpNo}
		 , #{category}
		 , #{title}
		 , #{responsibility}
		 , #{qualification}
		 , #{preferential}
		 , #{salary}
		 , #{benefits}
		 , #{introduce}
		 , #{sector}
		 , #{address}
		 , #{career}
		 , #{deadline}
	)

mapper.update:
   UPDATE EMPLOYMENT
  	SET
  		CATEGORY = #{category}
		, TITLE = #{title}
		, RESPONSIBILITY = #{responsibility}
		, QUALIFICATION = #{qualification}
		, PREFERENTIAL = #{preferential}
		, SALARY = #{salary}
		, BENEFITS = #{benefits}
		, INTRODUCE = #{introduce}
		, SECTOR = #{sector}
		, ADDRESS = #{address}
		, CAREER = #{career}
		, DEADLINE = #{deadline}
		, MODIFY_DATE = SYSDATE
  	WHERE NO = #{no}
  	AND CORP_NO = #{corpNo}
