mapper.insert:
  	INSERT INTO CORPORATE_MEMBER
	(
	    NO
		, ID
		, PWD
		, COMPANY_NAME
		, NAME
		, PHONE
		, ENROLL_DATE
	)
	VALUES
	(	
		SEQ_CORPORATE_MEMBER_NO.NEXTVAL
		 , #{id}
		 , #{pwd}
		 , #{companyName}
		 , #{name}
		 , #{phone}
		 , SYSDATE
	)

mapper.select:
SELECT ID 
FROM CORPORATE_MEMBER 
WHERE ID = #{id}

mapper.update:
UPDATE CORPORATE_MEMBER
SET
	COMPANY_NAME = #{companyName}
	, COMPANY_NUM = #{companyNum}
	, COMPANY_ADDRESS = #{companyAddress}
	, DETAIL_ADDRESS = #{detailAddress}
	, INTRODUCE = #{introduce}
	, SECTOR = #{sector}
	, EMP_NUM = #{empNum}
	, SALES = #{sales}
	, HOMEPAGE = #{homepage}

mapper.insert:
  	INSERT INTO EMPLOYMENT
  	(
	    NO
		, CORP_NO
		, CATEGORY
		, TITLE
		, RESPONSIBILITY
		, QUALIFICATION
		, PREFERENTIAL
		, SALARY
		, BENEFITS
		, INTRODUCE
		, SECTOR
		, ADDRESS
		, CAREER
		, DEADLINE
	)
	VALUES
	(	
		SEQ_EMPLOYMENT_NO.NEXTVAL
		 , #{corpNo}
		 , #{category}
		 , #{title}
		 , #{responsibility}
		 , #{qualification}
		 , #{preferential}
		 , #{salary}
		 , #{benefits}
		 , #{introduce}
		 , #{sector}
		 , #{address}
		 , #{career}
		 , #{deadline}
	)
