mapper.update:
		   MERGE INTO RESUME
			USING DUAL
			ON 
			(
				NO = #{no}
				AND MEMBER_NO = #{memberNo}
			)
		WHEN MATCHED THEN
			UPDATE SET
                CATEGORY = #{category}
                , NAME = #{name}
                , PHONE = #{phone}
                , EMAIL = #{email}
				, ADDRESS = #{address}
                , DETAIL_ADDRESS = #{detailAddress}
				, LINK = #{link}
				, INTRODUCE = #{introduce}
				, MODIFY_DATE = SYSDATE
                , ATTACH = #{attachName}
		WHEN NOT MATCHED THEN
			INSERT
			(
				NO
                , MEMBER_NO
                , CATEGORY
                , NAME
                , PHONE
                , EMAIL
                , ADDRESS
                , DETAIL_ADDRESS
                , LINK
                , INTRODUCE
                , ENROLL_DATE
                , ATTACH
			)
			VALUES
			(
				SEQ_RESUME_NO.NEXTVAL
				, #{memberNo}
				, #{category}
				, #{name}
				, #{phone}
				, #{email}
				, #{address}
				, #{detailAddress}
				, #{link}
				, #{introduce}
				, SYSDATE
				, #{attachName}
			)

mapper.select:
SELECT NO
FROM RESUME
WHERE MEMBER_NO = #{value}

