			, MAJOR
			, ENTER
			, GRADUATE
		)
		VALUES
		(
			GET_EDUCATION_SEQ()
			, #{ev.resumeNo}
			, #{ev.status}
			, #{ev.education}
			, #{ev.schoolName}
			, #{ev.major}
			, #{ev.enterSchool}
			, #{ev.graduate}
		)

;
END;

