mapper.insert:
insert into review_lec
(
    NO
	, WRITER
	, SCORE
	, CONTENT
	, ENROLL_DATE
	, CLASS_NO
)
values
(
    SEQ_REVIEW_LEC_NO.NEXTVAL
    , #{writer}
    , #{score}
    , #{content}
    , SYSDATE
    , #{bno}
)

mapper.update:
update review_lec
set
	score = #{score}
	, content = #{content}
	, ENROLL_DATE = SYSDATE
where no = #{rno}

mapper.select:
		select count(*) from REVIEW_LEC
    	where WRITER = #{mno}
    	and CLASS_NO = #{bno1}
    	and DELETE_YN = 'N'
