mapper.select:
SELECT ID 
FROM MEMBER
WHERE ID = #{memberId}

SELECT NICK
FROM MEMBER
WHERE NICK = #{memberNick}

SELECT ID
FROM MEMBER
WHERE NAME = #{memberName}
AND EMAIL = #{email}

mapper.update:
  UPDATE MEMBER
	SET 
	PROFILE_IMG_NAME = #{profileImgName}
	,PWD = #{memberPwd}
	,NAME = #{memberName}
	,PHONE = #{phone}
	,NICK = #{memberNick}
	,MODIFY_DATE = TO_CHAR(SYSDATE,'YY.MM.DD HH24:MI')
	WHERE NO = ${memberNo}

mapper.select:
SELECT PWD
FROM MEMBER
WHERE NO = ${memberNo}

mapper.update:
UPDATE MEMBER
SET DELETE_YN = 'Y'
WHERE NO = ${memberNo}
